import React, { Component } from "react";

class Download extends Component {
  render() {
    return (
      <div>
        <h2>DOWNLOAD THE GAME!</h2>
        <div className="boxwrapper">
        <div className="box">
        <LoginForm/>
        </div>
        </div>
      </div>
    );
  }
}

class LoginForm extends Component {
      constructor(props) {
    super(props);
    this.state = {
      userName: '',
      password: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      userName: event.target.userName
    });
  }
  handlePassword(event) {    this.setState({password: event.target.password});  }
  handleSubmit(event) {
    alert('Username entered: ' + this.state.value +
  '\nPassword entered: ' + this.state.password +
'\n\nClick OK to share this on the dark web.');
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Javabog username:
          <input type="text" value={this.state.username} onChange={this.handleChange} />
          </label>
        <label>
          Password:
          <input type="PASSWORD" password={this.state.password} onChange={this.handleChange} />
          </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default Download;
